/*LM EVENT BANNER COMPONENT */
(function() {

    'use strict';

    function initComp($comp) {

        var bannerDiv = $comp, //$(".banner")  
            bannerContent = $(".banner-content", $comp),
            bannerBackground = $(".cnt-event-banner", $comp),
            backgroundImage = bannerContent.attr("data-desktop-image"),
            eventLink = $(".event-link > a", $comp),
            eventURL = bannerContent.attr("action"),
            emailForm = $("#subscribe", $comp),
            buttonText = emailForm.attr("btn-text"),
            buttonColor = bannerContent.attr("data-button-type"),
            inputText = emailForm.attr("form-placeholder"),
            linkText = bannerContent.attr("action"),
            layoutOption = bannerContent.attr("data-layout-type"),
            emailOption = bannerContent.attr("data-email"),
            cookieExpirationInterval = bannerContent.attr("data-event-cookie-expiration-time"),
            eventCookieName = bannerContent.attr("data-event-cookie-name"),
            eventContentLastSavedDate = bannerContent.attr("data-event-cookie-content-date"),
            closeButton = $(".cta-close", $comp),
            footerDiv = $("footer");



        // SET AND REMOVE BACKGROUND IMAGES
        function setBackgroundImage() {
            if(!backgroundImage) {
                bannerBackground.css("background-image", "none");
            } else {
                bannerBackground.css("background-image", "url(" + mrm.utils.getImageURL(backgroundImage) + ")");
            }
        }
        
        
        // RESIZE BACKGROUND IMAGE AND FOOTER PADDING
        function resizeSticky() {
            $(window).resize(function() {
                var height = bannerDiv.height();

                footerDiv.css("padding-bottom", height);
                setBackgroundImage();
            });    
        }
        
        function resizeInline() {
            $(window).resize(function() {
                setBackgroundImage();
            });    
        }
        
        
        // SET BUTTON COLOR
        if (buttonColor == "clear-button") {
            eventLink.addClass("clear-button");  
        } else if (buttonColor == "blue-filled-button") {
            eventLink.addClass("light-blue-button");    
        } else {
            eventLink.addClass("orange-button");     
        };


        
        //LAYOUT OPTION: EVENT BANNER INLINE CONTENT WIDTH
        if (layoutOption == "inline-content-width") {
            
            //email enabled
            if(emailOption == "enabled") {
                bannerDiv.addClass("email");
                setBackgroundImage();

                setTimeout(function() {
                    var height = bannerDiv.height();

                    $(".hs-button").attr("value", buttonText).text(buttonText);
                    $(".hs-input").attr("placeholder", inputText);
                    bannerDiv.fadeIn("slow");
                }, 500);

                resizeInline(); 

                
            //email disabled
            } else if(emailOption == "disabled") {
                eventLink.attr("href", eventURL);
                bannerDiv.addClass("event");
                setBackgroundImage();
                resizeInline(); 
            }
            
            
        //LAYOUT OPTION: EVENT BANNER INLINE FULL WIDTH
        } else if (layoutOption == "inline-full-width") {
            
            //email enabled
            if(emailOption == "enabled") {
                
                var checkFormExist = setInterval(function() {
                    if ( $('.hbspt-form').length > 0 ) {
                        bannerDiv.addClass("email full-width");
                        setBackgroundImage();

                        setTimeout(function() {
                            var height = bannerDiv.height();

                            $(".hs-button").attr("value", buttonText).text(buttonText);
                            $(".hs-input").attr("placeholder", inputText);
                            bannerDiv.fadeIn("slow");
                        }, 250);
                        clearInterval(checkFormExist); 
                    }
                }, 100); 
                
                resizeInline(); 

                
            //email disabled
            } else if(emailOption == "disabled") {
                eventLink.attr("href", eventURL);
                bannerDiv.addClass("event full-width");
                setBackgroundImage();
                resizeInline(); 
            }
            
            
        //LAYOUT OPTION: EVENT BANNER STICKY TO BOTTOM OF WINDOW OR DEFAULT
        } else {
            var cookieEventValue = Cookies.get(eventCookieName);

            // CHECK FOR COOKIE SET WHEN USER HAS VIEWED AND CLOSED COMPONENT AND HIDE
            if(cookieEventValue && cookieEventValue == eventContentLastSavedDate) {
                bannerDiv.hide();

            } else {
            
                //email enabled
                if(emailOption == "enabled") {

                    var checkBannerExist = setInterval(function() {
                        if ( $('.hbspt-form').length > 0 ) {
                            $(".hs-button").attr("value", buttonText).text(buttonText);
                            $(".hs-input").attr("placeholder", inputText);
                            setBackgroundImage();

                            setTimeout(function() {
                                var height = bannerDiv.height();

                                footerDiv.css("padding-bottom", height);
                                bannerDiv.fadeIn("slow");      
                            }, 500); 
                            clearInterval(checkBannerExist); 
                        }
                    }, 100); 

                    resizeSticky(); 

                    $comp.find('.cta-close').on('click', function(e) {
                        e.preventDefault();
                        footerDiv.css("padding-bottom", "0");
                        bannerDiv.fadeOut(850).css("height", "0");
                    });


                //email disabled
                } else if(emailOption == "disabled") {

                    var checkBannerExist = setInterval(function() {
                        if ( $("footer").length > 0 && bannerDiv.length > 0 ) {
                            var height = bannerDiv.height();

                            setBackgroundImage();
                            eventLink.attr("href", eventURL);
                            footerDiv.css("padding-bottom", height);
                            bannerDiv.addClass("event sticky").fadeIn("slow"); 
                            clearInterval(checkBannerExist); 
                        }      
                    }, 100);

                    resizeSticky(); 

                    $comp.find('.cta-close').on('click', function(e) {
                        e.preventDefault();
                        footerDiv.css("padding-bottom", "0");
                        bannerDiv.fadeOut(850).css("height", "0");
                    });
                }
              
                // EVENT HANDLER 
                // IF USER CLOSES EVENT BANNER, SET EVENT COOKIE TO HIDE FOR AUTHORED INTERVAL
                closeButton.on("click", function() {
                    var expirationTime = parseInt(cookieExpirationInterval); 

                    if(cookieExpirationInterval){
                        Cookies.set(eventCookieName, eventContentLastSavedDate, {expires: expirationTime});
                    }
                });
            } 
        }
    }

    $('.banner').each(function(i) {
        initComp($(this));
    });

}());

