    function getResponsiveImageDom(containerSelector, imgLink) {
        var re = /(?:\.([^.]+))?$/;

        if (typeof re.exec(imgLink)[1] !== "undefined") {
            var imgFileExtension = re.exec(imgLink)[1].toLowerCase();

            // Have to do this because the mdm utility class provides the FULL image url
            // in some instances.
            if (imgLink.indexOf(".pc-adaptive") !== -1) {
            	var imageLink = imgLink.split(".pc-adaptive")[0];
            } else {
                var imageLink = imgLink;
            }

            var options = [
                {
                    containerMinWidth: 0,
                    containerMaxWidth: 480,
                    imgSrc: imageLink + '.pc-adaptive.480.high.' + imgFileExtension
                },
                {
                    containerMinWidth: 481,
                    containerMaxWidth: 768,
                    imgSrc: imageLink + '.pc-adaptive.768.medium.' + imgFileExtension
                },
                {
                    containerMinWidth: 769,
                    containerMaxWidth: 990,
                    imgSrc: imageLink + '.pc-adaptive.990.medium.' + imgFileExtension
                },
                {
                    containerMinWidth: 991,
                    containerMaxWidth: 1280,
                    imgSrc: imageLink + '.pc-adaptive.1280.medium.' + imgFileExtension
                },
                {
                    containerMinWidth: 1281,
                    containerMaxWidth: 1920,
                    imgSrc: imageLink + '.pc-adaptive.1920.medium.' + imgFileExtension
                },
                {
                    containerMinWidth: 1921,
                    containerMaxWidth: 9999999999,
                    imgSrc: imageLink + '.pc-adaptive.full.medium.' + imgFileExtension
                }
            ];

            $(containerSelector).trueResponsiveImage(options);
        }
    }

