(function($) {
    'use strict';

    function initComp($comp) {

        /**
         * Create the flickr Image URL
         * @param  {object} obj image object returned in the json
         * @return {string}     The url
         */
        function imageURL(obj) {
            return ['https://farm',
                obj.farm,
                '.staticflickr.com/',
                obj.server,
                '/',
                obj.id,
                '_',
                obj.secret,
                '_c.jpg'
            ].join('');
        }

        /**
         * Create the HTML fragments for the gallery slides
         * @param  @param  {object} obj image object returned in the json
         * @return {string}         The HTML fragment
         */
        function template(obj) {
            var url = imageURL(obj);
            return [
                ' <div class="slide"><span class="img-wrap"><img src="',
                url,
                '" /></span><div class="overlay"><span class="download"><a href="',
                url,
                '" download title="Download"><i class="fa fa-download" aria-hidden="true"></i></a></span><span class="caption">',
                obj.title,
                '</span></div></div>'
            ].join('');
        }

        /**
         * Create the carousel for the gallery
         * @return {void}
         */
        function createCarousel() {
            var slickSettings = {
                variableWidth: true,
                adaptiveHeight: false,
                centerMode: true,
                centerPadding: '240px',
                slidesToShow: 1,
                responsive:[{
                    breakpoint: 990,
                    settings: {
                        variableWidth: true,
                        centerPadding: '120px',
                        arrows:false
                    }
                }]
            };


            if ($('html').css('direction') == 'rtl') {
                // Slick requires this attribute for RTL to work correctly
                $('html').attr("dir", "rtl");

                $.extend(true, slickSettings, {
                    rtl:true,
                    prevArrow: '<button type="button" data-role="none" class="slick-prev" aria-label="Previous" tabindex="0" role="button"><i class="fa fa-angle-right" aria-hidden="true"></i></button>',
                    nextArrow: '<button type="button" data-role="none" class="slick-next" aria-label="Next" tabindex="0" role="button"><i class="fa fa-angle-left" aria-hidden="true"></i></button>'
                });
            }

            $('.slides', $comp).slick(slickSettings);

            $('.overlay', $comp).on("mousedown", function(e) {
                e.stopPropagation();
            });
        }

        /**
         * Create the api URL for the album
         * @return {string} The URL
         */
        function getAlbumURL() {
            var id = $comp.data('flickr_path')||'';
            id = id.substr(id.lastIndexOf('/')+1);
            var count = $comp.data('count') || 10;
            var user_id = $comp.data('user_id') || '10062223%40N06'; // if used for other clients need data attribute for user id.  Default: Lockheed
            var api_key = $comp.data('api_key');

            if(!api_key){
                console.error('ERROR: Gallery Component - API Key is required');
                return;
            }

            return ['https://api.flickr.com/services/rest/?method=flickr.photosets.getPhotos&api_key=',
            api_key,
            '&photoset_id=',
            id,
            '&per_page=',
            count,
            '&page=1&user_id=',
            user_id,
            '&format=json&nojsoncallback=1'].join('');
        }

        /**
         * Start things off by requesting the json from the flickr server.
         * @return {void}
         */
        function init() {
            if($comp.data('type') === 'flickr'){
                var url = getAlbumURL();
                if(url){
                    $.ajax({
                        url: url,
                        dataType: 'json'
                    })
                    .done(function(data) {
                        if(data.stat !== 'ok') return;
                        var html = [];
                        data.photoset.photo.forEach(function(e, i, c) {
                            html.push(template(e));
                        });

                        $('.slides', $comp).empty().append(html);
                        createCarousel();
                    })
                    .fail(function() {
                        console.log("API Failed to load");
                    });
                }
            }else if($comp.find('.slides').children('.slide').length){
                createCarousel();
            }
        }

        init();
    }

    /**
     * find all galleries on the page on initalize them
     */
    $('.cnt_gallery').each(function(index, el) {
        initComp($(this));
    });
})(jQuery);

