(function() {

    'use strict';

    function initComp($comp) {
        var navIdArray = [];

        //Toggle function for nav and content
        var toggleContent = function(linkId, autoScroll) {
            var $tabLink = $('.tab-content__navigation-link', $comp).filter('[data-tab-id="' + linkId + '"]');
            var $tabContainer = $('.tab-content__container', $comp).filter('[data-content-id="' + linkId + '"]');
            var $accordionLink = $('.tab-content__accordion-link', $comp).filter('[data-accordion-id="' + linkId + '"]');

            $('.tab-content__navigation-link', $comp).removeClass('active');
            $('.tab-content__container', $comp).removeClass('active');
            $('.tab-content__accordion-link', $comp).removeClass('active');

            if (linkId) {
                $tabLink.addClass('active');
                $tabContainer.addClass('active');
                $accordionLink.addClass('active');

                if (autoScroll) {
                    if ($accordionLink.offset().top > 0) {

                        //Delay for correct offset top value
                        setTimeout(function() {
                            $("html, body").animate({ scrollTop: $accordionLink.offset().top }, 300);
                        }, 500);

                    } else {
                        $("html, body").animate({ scrollTop: $comp.offset().top }, 300);
                    }
                }
            }
        };

        //Event binding function for window and accordion events
        var bindEvents = function(callback) {

            $(window).on('hashchange', function(e) {
                e.preventDefault();
                var locationId = location.hash.slice(1);
                var isValidId = $.inArray(locationId, navIdArray);

                if (locationId === '' || isValidId !== -1) {
                    toggleContent(locationId, true);
                }
                return false;
            });

            $('.tab-content__accordion-link', $comp).on('click', function(e) {
                if ($(this).attr('href') === location.hash || $(this).hasClass('active')) {
                    e.preventDefault();
                    var scrollPosition = $(window).scrollTop();
                    window.location.hash = '';
                    $(window).scrollTop(scrollPosition);
                    $('.tab-content__container', $comp).removeClass('active');
                    $('.tab-content__accordion-link', $comp).removeClass('active');
                }
            });

            callback();
        };

        //Setup component 
        var setUp = function() {

            //Find and store valid ids so that none matching hash values can be ignored
            $('.tab-content__accordion-link', $comp).each(function(index, item) {
                navIdArray.push($(item).data('accordion-id'));
            });

            var locationId = location.hash.slice(1);
            var isValidId = $.inArray(locationId, navIdArray);

            //Bind events then load default tab
            bindEvents(function() {
                if (isValidId !== -1) {
                    toggleContent(locationId);
                } else {
                    toggleContent(navIdArray[0]);
                }
            });
        };

        setUp();
    }

    $('.tab-content').each(function(i) {
        initComp($(this));
    });

}());
